#1)Sum of Two numbers\n","a=10\n","b=90\n","sum=a+b\n","print(\"The sum is\",sum)\n","\n","#Method_2\n","num1=int(input(\"Enter the number : \"))\n","num2=int(input(\"Enter the number : \"))\n","sum=num1+num2\n","print(\"The sum of\",num1, \"and\",num2 ,\"is\",sum)"],"metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"9ZFpyfMhT1UY","executionInfo":{"status":"ok","timestamp":1730893871657,"user_tz":-330,"elapsed":963,"user":{"displayName":"Flemina Sasidharan","userId":"04076310907761490698"}},"outputId":"25d9e430-bce0-4f0d-b412-541f0d924cdc"},"execution_count":1,"outputs":[{"output_type":"stream","name":"stdout","text":["The sum is 100\n"]}]},{"cell_type":"code","source":["#2-Difference between two numbers\n","a=234\n","b=123\n","diff=a-b\n","print(\"The difference between 234 and 123 is\",diff)\n","\n","#Method_2\n","num1=int(input(\"Enter the number : \"))\n","num2=int(input(\"Enter the number : \"))\n","diff=num1-num2\n","print(\"The difference between\",num1, \"and\",num2 ,\"is\",diff)"],"metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"IF_MAe4SUJLu","executionInfo":{"status":"ok","timestamp":1730893916746,"user_tz":-330,"elapsed":11,"user":{"displayName":"Flemina Sasidharan","userId":"04076310907761490698"}},"outputId":"a4d0f113-fbd2-4915-d2b2-959c7cdaba2b"},"execution_count":2,"outputs":[{"output_type":"stream","name":"stdout","text":["The difference between 234 and 123 is 111\n"]}]},{"cell_type":"code","source":["#3-Product of two numbers\n","a=589\n","b=12\n","pro=a*b\n","print(\"The product of 589 and 12 is\",pro)\n","\n","#Method_2\n","num1=int(input(\"Enter the number : \"))\n","num2=int(input(\"Enter the number : \"))\n","pro=num1*num2\n","print(\"The product of\",num1, \"and\",num2 ,\"is\",pro)"],"metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"bn9jPylHUZLo","executionInfo":{"status":"ok","timestamp":1730893979166,"user_tz":-330,"elapsed":485,"user":{"displayName":"Flemina Sasidharan","userId":"04076310907761490698"}},"outputId":"23915218-4f01-45ae-9432-9f529938b539"},"execution_count":3,"outputs":[{"output_type":"stream","name":"stdout","text":["The product of 589 and 12 is 7068\n"]}]},{"cell_type":"code","source":["#4-Division of two numbers\n","a=456\n","b=3\n","quo=a/b\n","print(\"The quotient of 456 by 3 is\",quo)\n","\n","#Method_2\n","num1=int(input(\"Enter the number : \"))\n","num2=int(input(\"Enter the number : \"))\n","q=num1/num2\n","print(\"The quotient of\",num1, \"and\",num2 ,\"is\",q)"],"metadata":{"id":"7YFgGK_gUn5Y"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#5-Program to find the area of a cirlce\n","r=int(input(\"Enter the radius of the circle : \")) #Input the radius from the user\n","area=3.14*r*r #Equation to find the area of the circle\n","print(\"Area of the circle with radius\",r,\" = \",area) #To print the area of the circle as the output"],"metadata":{"id":"SBMHHc1RVb8U"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#6-Program to find the circumference of a circle\n","r=int(input(\"Enter the radius of the circle: \")) #Input the radius from the user\n","c=2*3.14*r #Equation to find the circumference of a circle\n","print(\"Circumference of circle with radius\",r,\"=\",c)"],"metadata":{"id":"QylcHKzxViEo"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#7-Program to find the area of a rectangle\n","l=int(input(\"Enter the length of the rectangle: \")) #Input the length of the rectangle from the user\n","b=int(input(\"Enter the breadth of the rectangle: \")) #Input the breadth of the rectangle from the user\n","area=l*b #Equation to find the area of the rectangle\n","print(\"Area of the rectangle with length =\",l,\"and breadth =\",b,\"is\",area) #To print the area of the rectangle as the output\n"],"metadata":{"id":"kkIktAP0Vq12"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#8-Program to find the area of square\n","s=int(input(\"Enter the side of the square: \")) #Input of the side of the square\n","area=s*s #Equation to find the area of the square\n","print(\"Area of the square with side\",s,\"is\",area) #Print the area of square as ouput"],"metadata":{"id":"xsF2YdzcVyu_"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#9-Program to find the area of the triangle\n","b=int(input(\"Enter the base length of the triangle: \")) #User input for the base of the triangle\n","h=int(input(\"Enter the height of the triangle: \")) #User input for the height of the triangle\n","area=0.5*(b*h) #Equation to find the area of the triangle\n","print(\"Area of the triangle with base =\",b,\"and height =\",h,\"is\",area) #To print the area of the triangle as the output"],"metadata":{"id":"dEn44kJpWBJc"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#10-Swapping values in a variable\n","#Method_1\n","#With temp variable\n","a=9\n","b=10\n","temp=a\n","a=b\n","b=temp\n","print(a)\n","print(b)\n","\n","\n","#Method_2\n","#Without temp variable\n","a=6\n","b=3\n","s=a+b\n","a=s-a\n","b=s-b\n","print(a)\n","print(b)\n","\n","\n","#Method_3\n","#Without arithmetic operation\n","a=6;b=5\n","a,b=b,a\n","print(a)\n","print(b)\n"],"metadata":{"id":"77gbqIGvWG58"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#11-b**2-4*a*c\n","a=int(input(\"Enter the value of a: \"))\n","b=int(input(\"Enter te value of b: \"))\n","c=int(input(\"Enter the value of c: \"))\n","s=b**2-4*a*c\n","print(s)"],"metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"RhKeiCq7WXW7","executionInfo":{"status":"ok","timestamp":1730894523477,"user_tz":-330,"elapsed":23598,"user":{"displayName":"Flemina Sasidharan","userId":"04076310907761490698"}},"outputId":"22fd810f-606a-4403-d26e-62c2ef6581be"},"execution_count":4,"outputs":[{"output_type":"stream","name":"stdout","text":["Enter the value of a: 1\n","Enter te value of b: 3\n","Enter the value of c: 3\n","-3\n"]}]},{"cell_type":"code","source":["#12-Net salary\n","salary=int(input(\"Enter the basic salary: \"))\n","da=0.15*salary\n","hra=0.25*salary\n","pf=0.03*salary\n","net_salary=salary+da+hra-pf\n","print(\"The Basic Salary of the individual is \",salary)\n","print(\"The DA (Dearness Allowance) is \",da)\n","print(\"The HRA (House Rent Allowance) is \",hra)\n","print(\"The PF (Provident Fund) is \",pf )\n","print(\"Hence the Net Salary of the individual is \",net_salary)"],"metadata":{"id":"7-r-9of1WsvI"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#13-Biggest among two numbers ...\n","a=int(input(\"Enter the first number: \")) #Input number1 from user\n","b=int(input(\"Enter the second nunmber: \")) #Input number2 from user\n","if a>b:\n","  print(a,\"is greater than\",b)\n","elif b>a:\n","  print(b,\"is greater than\",a)\n","else:\n","  print(\"Both are equal\")"],"metadata":{"id":"6WdyrcYYW2ob"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#14- Find the Largest of Three Numbers\n","\n","a=int(input(\"Enter the first number: \"))\n","b=int(input(\"Enter the second number: \"))\n","c=int(input(\"Enter the third number: \"))\n","if a>b and a>c:\n","  print(a,\"is the largest among the three numbers\")\n","elif b>a and b>c:\n","  print(b,\"is the largest among the three numbers\")\n","elif c>a and c>b:\n","  print(c,\"is the largest among the three numbers\")\n","else:\n","  print(\"All the three numbers are equal\")"],"metadata":{"id":"zUhvFaZGW8c3"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#15-Net_Salary using simple if and if else\n","\n","salary=int(input(\"Enter the basic salary: \"))\n","if salary<10000:\n","  da=0.25*salary\n","  hra=0.30*salary\n","  pf=0.08*salary\n","elif 10000<salary<20000:\n","  da=0.20*salary\n","  hra=0.25*salary\n","  pf=0.06*salary\n","elif 20000<salary<30000:\n","  da=0.15*salary\n","  hra=0.20*salary\n","  pf=0.04*salary\n","else:\n","  da=0.10*salary\n","  hra=0.15*salary\n","  pf=0.02*salary\n","net_salary=salary+da+hra-pf\n","print(\"The Basic Salary of the individual is \",salary)\n","print(\"The DA (Dearness Allowance) is \",da)\n","print(\"The HRA (House Rent Allowance) is \",hra)\n","print(\"The PF (Provident Fund) is \",pf )\n","print(\"Hence the Net Salary of the individual is \",net_salary)"],"metadata":{"id":"zAabmMTMXF5y"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#16-Calculator\n","\n","a=int(input(\"Enter the first number: \"))\n","b=int(input(\"Enter the second number: \"))\n","print(\"Select the option number from the following\")\n","print(\"1 : Addition \\n2 : Subtraction \\n3 : Multiplication \\n4 : Division \\n5 : Exponent \")\n","n=int(input(\"Enter the option number: \"))\n","if n==1:\n","    print(f\"The sum of two numbers {a} and {b} is \",a+b)\n","elif n==2:\n","    if a<b:\n","      a,b=b,a\n","    print(f\"The difference between two numbers {a} and {b} is \",a-b)\n","elif n==3:\n","    print(f\"The product of two numbers {a} and {b} is \",a*b)\n","elif n==4:\n","    if b==0:\n","      print(\"Division by Zero is not possible\")\n","    else:\n","      print(f\"The quotient when two numbers {a} and {b} get divided is \",a/b)\n","elif n==5:\n","    print(f\"The exponent of {a} raised to {b} is \",a**b)\n","else:\n","    print(\"No valid option selected\")\n","\n"],"metadata":{"id":"AuL1-Ri4XTVn"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#17-Odd or Even using simple if and else\n","n=int(input(\"Enter the number: \"))\n","if n%2==0:\n","  print(\"The number\",n,\"is an even number\")\n","else:\n","  print(\"The number\",n,\"is an odd number\")"],"metadata":{"id":"Y21MwKvxXZdH"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#18-Calculate Tax Based on Income\n","\n","income=float(input(\"Enter your annual income: \"))\n","if income<=50000:\n","  tax=0.05*income\n","elif income<=100000:\n","  tax=0.10*income\n","else:\n","  tax=0.15*income\n","print(\"The tax for your income is \",tax)"],"metadata":{"id":"lzhUOT31Xk7S"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#19-Identify the Type of Triangle by Side Lengths-python\n","\n","a=int(input(\"Enter the side_1 of the triangle: \"))\n","b=int(input(\"Enter the side_2 of the triangle: \"))\n","c=int(input(\"Enter the side_3 of the triangle: \"))\n","if a==b==c:\n","  print(\"The triangle is an Equilateral Triangle\")\n","elif a==b or b==c or a==c:\n","  print(\"The triangle is an Isosceles Triangle\")\n","else:\n","  print(\"The triangle is a Scalene Triangle\")"],"metadata":{"id":"EwC0tNiXXwjE"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#20-Check the given measurement is of a right angle triangle or not\n","\n","a=int(input(\"Enter the first angle of the triangle :  \"))\n","b=int(input(\"Enter the second angle of the triangle :  \"))\n","c=int(input(\"Enter the third angle of the triangle :  \"))\n","while a+b+c==180:\n","  if a==90 or b==90 or c==90:\n","    print(\"The triangle is a right-angled triangle\")\n","    break\n","  else:\n","    print(\"The triangle is not a right-angled triangle\")\n","    break\n","else:\n","  print(\"The values shows that this is not suitable to form a triangle. Sum of all angles must be equal to 180\")"],"metadata":{"id":"qGsK_ez8X7_7"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#21-Electricity Bill\n","\n","unit=int(input(\"Enter the amount of electricity unit used: \"))\n","if unit<=100:\n","  amount=0.5*unit\n","elif 101<=unit<=150:\n","  amount=(100*0.5)+((unit-100)*0.75)\n","elif 151<=unit<=200:\n","  amount=(100*0.5)+(50*0.75)+((unit-150)*1)\n","else:\n","  amount=(100*0.5)+(50*0.75)+(50*1)+((unit-200)*2)\n","print(\"The bill amount =\",amount)"],"metadata":{"id":"NU521SIWYKsH"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#22-Simple Interest Calculation\n","p=int(input(\"Enter the principal amount: \"))\n","t=int(input(\"Enter the time period in years: \"))\n","if p>=200000 or t>=5:\n","  r=0.12\n","else:\n","  r=0.09\n","si=(p*r*t)/100\n","print(\"The simple interest for the amount is \",si)"],"metadata":{"id":"gwBj-Pp-YX5I"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#23-Print numbers from 1 to 10\n","n=1\n","while n<11:\n","  print(n)\n","  n+=1"],"metadata":{"id":"EyPrrn6JYkNl"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#24-Print odd numbers from 1 to 20\n","n=1\n","while n<20:\n","  if n%2!=0:\n","    print(n)\n","  n+=1"],"metadata":{"id":"HUADY40MYwsH"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#25-Print the sum of n natural numbers\n","n=int(input(\"Enter the limit: \"))\n","i=1\n","temp=i\n","sum=0\n","while i<=n:\n","  sum+=i\n","  i+=1\n","print(\"The sum of natural numbers from\",temp,\"to\",n,\"is\",sum)"],"metadata":{"id":"aldxGM7xY2KA"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#26-Multiplication Table\n","\n","n=int(input(\"Enter the number of which multiplication table is to be displayed: \"))\n","i=1\n","while i<=10:\n","  print(i ,\" * \",n,\" = \",i*n)\n","  i+=1"],"metadata":{"id":"ccNDV-8xY-Kq"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#27-Factorial of a number\n","n=int(input(\"Enter the number of which factorial is to be displayed : \"))\n","i=1\n","fact=1\n","while i<=n:\n","  fact=fact*i\n","  i+=1\n","print(n,\"! = \",fact)\n"],"metadata":{"id":"ul8eUQmzZIWt"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#28-Fibonacci Series\n","n=int(input(\"Enter the limit : \"))\n","f=0\n","s=1\n","if n==1:\n","  print(f)\n","elif n==2:\n","  print(f)\n","  print(s)\n","elif n>=3:\n","    print(f)\n","    print(s)\n","    i=2\n","    while i<n:\n","      t=f+s\n","      print(t)\n","      f,s=s,t\n","      i+=1\n","\n"],"metadata":{"id":"3RbGMpdBZNZK"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#29-Quadratic_equation\n","\n","a=int(input(\"Enter the value of a : \"))\n","b=int(input(\"Enter the value of b : \"))\n","c=int(input(\"Enter the value of c : \"))\n","x=(b**2)-(4*a*c)\n","if x==0:\n","    x1=(-b)/(2*a)\n","    print(\"There is only one solution\")\n","    print(x1)\n","elif x>0:\n","    x2=-b+(x)/2*a\n","    x3=-b-(x)/2*a\n","    print(\"There are two solutions\")\n","    print(x2,x3)\n","else:\n","    print(\"Solution is a complex number with real and imaginary value\")\n"],"metadata":{"id":"96K786tGZh3I"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#30-Check if a year is leap year\n","\n","year=int(input(\"Enter the year: \"))\n","if(year%4==0 and year%100!=0) or (year%400==0):\n","    print(f\"The year {year} is a Leap Year\")\n","else:\n","    print(f\"The year {year} is not a Leap Year\")"],"metadata":{"id":"22kzQXzFZzkx"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#31-Reverse of a number\n","n=int(input(\"Enter the number : \"))\n","rev=0\n","while n!=0:\n","  d=n%10 #to get the number in the one's place\n","  rev=rev*10+d\n","  n=n//10\n","print(rev)\n"],"metadata":{"id":"-Y3JHIbqaKl5"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#32-Sum 0f digits\n","n=int(input(\"Enter the digit : \"))\n","temp=n\n","sum=0\n","while n!=0:\n","  d=n%10\n","  sum+=d\n","  n//=10\n","print(\"The sum of the digits of the number\",temp,\"=\",sum)"],"metadata":{"id":"mBycuLSDaObp"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#33-Sum of digits in a single digit\n","n=int(input(\"Enter the digit : \"))\n","temp=n\n","sum=0\n","while n!=0:\n","  d=n%10\n","  sum+=d\n","  n//=10\n","  if n==0 and sum>9:\n","    n=sum\n","    sum=0\n","print(sum)"],"metadata":{"id":"fybUKKWdaX7d"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#34-Write a program that checks if a given Number is a palindrome.\n","n=int(input(\"Enter the number : \"))\n","temp=n\n","rev=0\n","while n!=0:\n","  d=n%10\n","  rev=rev*10+d\n","  n=n//10\n","if rev==temp:\n","  print(\"The given number\",temp,\"is a palindrome\")\n","else:\n","  print(\"The given number\",temp,\"is not a palindrome\")"],"metadata":{"id":"_46YkjWIalf7"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#35-Write a program that counts the number of digits in a given number.\n","\n","n=int(input(\"Enter the number : \"))\n","count=0\n","while n!=0:\n","  n//=10\n","  count+=1\n","print(count)"],"metadata":{"id":"K5NqYYtUa34E"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#36-Write a program that finds the largest digit in a given number.\n","n=int(input(\"Enter the number : \"))\n","largest=0\n","while n>0:\n","  d=n%10\n","  if largest<d:\n","    largest=d\n","  n//=10\n","print(\"The largest digit among the number is : \", largest)"],"metadata":{"id":"6SoDePwfa7XC"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#37-Write a program that checks if a given Number is a Prime\n","n=int(input(\"Enter the number : \"))\n","i=2\n","flag=0\n","while i<=(n/2):\n","  if (n%i)==0:\n","    flag=1\n","    # break\n","  i+=1\n","if n==1:\n","  print(\"1 is neither prime nor composite\")\n","elif flag==1:\n","  print(n,\"is not a prime number\")\n","elif flag==0:\n","  print(n,\"is a prime number\")\n"],"metadata":{"id":"-oue-yz6bG4I"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#38-Write a program that calculates the sum of all even numbers between 1 and a given number n\n","s=1\n","n=int(input(\"Enter the limit number : \"))\n","sum=0\n","while s<=n:\n","  if s%2==0:\n","    sum+=s\n","  s+=1\n","print(\"The sum of even numbers between 1 and\",n,\"is\" ,sum)"],"metadata":{"id":"jY7HiuGabLOq"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#39-Armstrong number or not\n","n=int(input(\"Enter the number : \"))\n","count=0\n","sum=0\n","temp1=n\n","temp2=n\n","while temp1!=0:\n","  count+=1\n","  temp1//=10\n","\n","while temp2>0:\n","  digit=temp2%10\n","  sum+=digit**count\n","  temp2//=10\n","\n","if n==sum:\n","  print(n,\"is an Armstrong Number\")\n","else:\n","  print(n,\"is not an Armstrong Number\")\n","\n"],"metadata":{"id":"guOKx17qbuLE"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#40-Perfect Number\n","# n=int(input(\"Enter the number : \"))\n","# sum=0\n","# i=1\n","# while i<n:\n","#   if n%i==0:\n","#     sum+=i\n","#   i+=1\n","# if n==sum:\n","#   print(n,\"is a Perfect Number\")\n","# else:\n","#   print(n,\"is not a Perfect Number\")\n","\n","n=int(input(\"Enter the number : \"))\n","s=0\n","for i in range(1,n):\n","    if n%i==0:\n","      s+=i\n","if s==n:\n","  print(n,\"is a perfect number\")\n","else:\n","  print(n,\"is not a perfect number\")"],"metadata":{"id":"wFaw8SS8byvh"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#41-Reverse of a string using slicing\n","\n","# a=\"python\"[::-1]\n","# print(a)\n","\n","a=\"python\"\n","rev=\"\"\n","for i in a:\n","  rev=i+rev\n","print(rev)"],"metadata":{"id":"-65QchBccBpd"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#42-vowels in string\n","a=\"anytime\"\n","v=\"aeiouAEIOU\"\n","cv=0\n","cn=0\n","for i in a:\n","  if i in v:\n","    cv+=1\n","  else:\n","    cn+=1\n","print(\"Count of vowels = \",cv)\n","print(\"Count of non-vowels = \",cn)"],"metadata":{"id":"TEOJ_e5BcEkN"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#43-Biggest among given n numbers\n","\n","limit=int(input(\"Enter the total count numbers : \"))\n","l=0\n","for i in range(limit):\n","  a=int(input(f\"Enter the number {i+1}: \"))\n","if l<a:\n","  l=a\n","print(\"The largest number among this is\", l)"],"metadata":{"id":"jm5EjCcbcM3D"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#44-Prime numbers within a limit\n","a=int(input(\"Enter the lower limit : \"))\n","b=int(input(\"Enter the upper limit : \"))\n",